import { useQuery } from "react-query";
import './Data.css';

const Data = () => {

    //필요한 데이터 요청
    const payment_currency = "KRW";

    //isLoading, error, data, isFetching
    const {status, data, error} = useQuery(
        payment_currency,() => 
        fetch(`https://api.bithumb.com/public/transaction_history/${order_currency}_${payment_currency}`)
            .then((res) => res.json())
            .then((res) => res.data), 
            { refetchInterval: 1000 } // 1초마다 갱신
    );

    if (status === "loading") {
        return <span>Loading...</span>;
    }
    
    if (status === "error") {
        return <span>Error: {error.message}</span>;
    }
    // if(isLoading){
    //     return <span>Loading...</span>;
    // }
    // if(error){
    //     return <span>Error: {error.message}</span>;
    // }
    
    return (
    <div id="container">
        <div className='blue-nav'>
            <div>실시간 감시 운영 프로그램</div>
        </div>
        <div id="datas">
            {data.map((el, idx) => {
                return (
                <div className="data" key={idx}>
                    <p>거래 일시 : {el.transaction_date}</p>
                    <p>현재 가격 : {el.price + " " + payment_currency}</p>
                    {/* <p>거래 수량 : {el.units_traded + " " + order_currency}</p> */}
                    <p>거래 금액 : {el.total + " " + payment_currency}</p>
                </div>);
            })}
        </div>
    </div>);
}

export default Data;
